// Process and structure all review sources
const items = $input.all();
const prepareData = $('Prepare Search').first().json;

// Helper to safely get items
function safeGet(items, identifier) {
  return items.find(i => {
    const q = i.json?.searchParameters?.q || '';
    return q.includes(identifier);
  })?.json || {};
}

// Process Amazon reviews from Serper results
const amazonResults = safeGet(items, 'amazon.com');
const amazonReviews = (amazonResults.organic || []).map(r => ({
  title: r.title,
  snippet: r.snippet,
  url: r.link
}));

// Process YouTube results
const youtubeResults = items.find(i => i.json?.items)?.json || {};
const youtubeReviews = (youtubeResults.items || []).map(v => ({
  videoId: v.id?.videoId,
  title: v.snippet?.title,
  channel: v.snippet?.channelTitle,
  description: v.snippet?.description,
  thumbnail: v.snippet?.thumbnails?.high?.url,
  publishedAt: v.snippet?.publishedAt,
  url: `https://youtube.com/watch?v=${v.id?.videoId}`
}));

// Process Article reviews from expert sites
const articleResults = safeGet(items, 'rtings.com');
const articleReviews = (articleResults.organic || []).map(r => ({
  source: new URL(r.link).hostname.replace('www.', ''),
  title: r.title,
  snippet: r.snippet,
  url: r.link
}));

return {
  productName: prepareData.productName,
  model: prepareData.model,
  vendor: prepareData.vendor,
  category: prepareData.category,
  customerReviews: {
    source: 'Amazon',
    reviews: amazonReviews,
    totalFound: amazonReviews.length
  },
  youtubeReviews: {
    totalFound: youtubeReviews.length,
    videos: youtubeReviews.slice(0, 5) // Top 5 videos
  },
  articleReviews: {
    totalFound: articleReviews.length,
    articles: articleReviews
  },
  aggregatedAt: new Date().toISOString()
};

