// Parse GPT-4 analysis and combine with raw data
const rawResponse = $input.first().json.text || $input.first().json.message?.content || '';
const reviewData = $('Process All Reviews').first().json;

let analysis;
try {
  const jsonMatch = rawResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analysis = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found');
  }
} catch (e) {
  analysis = {
    customerSentiment: { overall: 'unknown', commonPraise: [], commonComplaints: [] },
    expertConsensus: { strengths: [], weaknesses: [], recommendations: '' },
    youtubeHighlights: { mostMentioned: [], concerns: [] },
    overallVerdict: rawResponse
  };
}

return {
  ...reviewData,
  analysis
};
